mapper.select:
select a.station_id station_id, a.name station_name, a.sido_code sido_code, b.sido_name sido_name
from station_info a,
     sido b
where a.sido_code = b.sido_code;

select a.station_id station_id, a.name station_name, a.sido_code sido_code, b.sido_name sido_name
from station_info a,
     sido b
where a.sido_code = b.sido_code and a.sido_code = #{sidoCode}

select station_id
from station_info
where name = #{name}
